import React, { Component } from "react";
import SimpleStorageContract from "./contracts/SimpleStorage.json";
import SbToken from "./contracts/SbToken.json"
import pool from "./contracts/pool.json"
import getWeb3 from "./getWeb3";

import "./App.css";



class App extends Component {
  //state = variables
  state = { storageValue: 0, web3: null, accounts: null, contract: null };

  componentDidMount = async () => {
    try {
      // Get network provider and web3 instance.
      const web3 = await getWeb3();

      // Use web3 to get the user's accounts.
      const accounts = await web3.eth.getAccounts();

      // Set web3, accounts, and contract to the state, and then proceed with an
      // example of interacting with the contract's methods.
      this.setState({ web3, accounts });
    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Failed to load web3 or accounts. Check console for details.`,
      );
      console.error(error);
    };



    await this.getSbTokenContract();
    await this.getPoolContract();
    //alert(this.state.poolContract);
    await this.getPoolSupply();

    await this.runSimpleStorage();


  };

  //convert token to wei
  tokenToWei = (n) => {
    return this.state.web3.utils.toWei(n, 'ether');
  }

  //convert wei to token
  weiToToken = (n) => {
    return this.state.web3.utils.fromWei(n, 'ether');
  }

  runSimpleStorage = async() =>{
    try {
      // Get network provider and web3 instance.
      const web3 = await getWeb3();

      // Use web3 to get the user's accounts.
      const accounts = await web3.eth.getAccounts();

      // Get the contract instance.
      const networkId = await web3.eth.net.getId();
      const deployedNetwork = SimpleStorageContract.networks[networkId];
      const instance = new web3.eth.Contract(
        SimpleStorageContract.abi,
        deployedNetwork && deployedNetwork.address,
      );


      // Set web3, accounts, and contract to the state, and then proceed with an
      // example of interacting with the contract's methods.
      this.setState({ web3, accounts, contract: instance }, this.runExample);
    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Failed to load web3, accounts, or contract. Check console for details.`,
      );
      console.error(error);
    }
  }

  getPoolContract = async() => {
    try {
      const web3 = await getWeb3();
      // Get the contract instance.
      const networkId = await web3.eth.net.getId();
      const deployedNetwork = pool.networks[networkId];
      const instance = new web3.eth.Contract(
        pool.abi,
        deployedNetwork && deployedNetwork.address,
      );


      // Set web3, accounts, and contract to the state, and then proceed with an
      // example of interacting with the contract's methods.
      this.setState({ poolContract: instance });
    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Failed to load pool contract. Check console for details.`,
      );
      console.error(error);
    }
  }

  getPool1 = async () => {
    const { accounts, contract } = this.state;
    //alert(accounts);
    // Stores a given value, 5 by default.

    const response1 = await contract.methods.approve(this.state.poolContract.options.address,'100').send({from:accounts[0]});
    const response2 = await this.state.poolContract.methods.settingUp(accounts[0],'100','0').send({from:accounts[0]});
    const response = await contract.methods.balanceOf(this.state.poolContract.options.address).call();

    this.setState({balancePool:response})

    // // Get the value from the contract to prove it worked.
    // const response = await contract.methods.get().call();

    // // Update state with the result.
    // this.setState({ storageValue: response });
  };

  //get sbtoken contract
  getSbTokenContract = async() => {


    try {  
      const web3 = await getWeb3();

      // Use web3 to get the user's accounts.
      const accounts = await web3.eth.getAccounts();

      // Get the contract instance.
      const networkId = await web3.eth.net.getId();
      const deployedNetwork = SbToken.networks[networkId];
      const instance = new web3.eth.Contract(
        SbToken.abi,
        deployedNetwork && deployedNetwork.address,
      );

      alert(instance);


      // Set contract to the state
      this.setState({ sbTokenContract: instance });
    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Failed to load sbtoken contract. Check console for details.`,
      );
      console.error(error);
    }
  }

  runExample = async () => {
    const { accounts, contract } = this.state;

    // Stores a given value, 5 by default.
    await contract.methods.set(5).send({ from: accounts[0] });

    // Get the value from the contract to prove it worked.
    const response = await contract.methods.get().call();

    // Update state with the result.
    this.setState({ storageValue: response });
  };

  getPoolSupply = async () => {
    const {accounts,sbTokenContract,poolContract} = this.state;
    // alert(this.state.poolContract);
    // Stores a given value, 5 by default.

    // await contract._mint('0x7e4aBb44a722a4445210cC8Ba5d84b56640dC7Ee','10000000000000000000000000000');


    //const response = await contract.methods.balanceOf(accounts[0]).call();
    await sbTokenContract.methods.approve(poolContract.options.address,'100').send({from:accounts[0]});
    await poolContract.methods.settingUp(accounts[0],'100','0').send({from:accounts[0]});
    const sbtBalance = await poolContract.sbtBalance.call();
    const ethBalance = await poolContract.ethBalance.call();

    this.setState({poolSbtBalance:sbtBalance, poolEthBalance:ethBalance});

    // // Get the value from the contract to prove it worked.
    // const response = await contract.methods.get().call();

    // // Update state with the result.
    // this.setState({ storageValue: response });
  };

  render() {
    if (!this.state.web3) {
      return <div>Loading Web3, accounts, and contract...</div>;
    }
    return (
      <div className="App">
        <h1>Good to Go!</h1>
        <p>Your Truffle Box is installed and ready.</p>
        <h2>Smart Contract Example</h2>
        <p>
          If your contracts compiled and migrated successfully, below will show
          a stored value of 5 (by default).
        </p>
        <p>
          Try changing the value stored on <strong>line 42</strong> of App.js.
        </p>
        <div>The stored value is: {this.state.storageValue}</div>
        <div>The ETH balance is: {this.state.ethBalance}</div>
        <div>The SBT balance is: {this.state.sbtBalance}</div>
      </div>
    );
  }
}

export default App;
